import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import {minutesToDuration, secondsToDuration} from "../utils/duration"
import { PropTypes } from "prop-types"


function Pomodoro() {
  // Timer starts out paused

  const [count, setCount] = useState(0)
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [timer, setTimer] = useState(1500)
  const [breakDuration,setBreakDuration] = useState(300);
  const [currentSession, setCurrentSession] = useState(`Focusing for 25:00 minutes`);
  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      setTimer(prevTime => {
        const newTime = prevTime - 1;
        if(newTime >= 0){
          return prevTime - 1
        }
        if(currentSession === "Focusing for 25:00 minutes"){
          setCurrentSession('On Break for 05:00 minutes');
        }else{
          setTimer(0)
        }
      })
      setBreakDuration(prevTime => {
        const newTime = prevTime - 1;
        if(newTime >= 0){
          return prevTime - 1
        }
        if(currentSession === "On Break for 05:00 minutes"){
          setCurrentSession('Focusing for 25:00 minutes');
        }else{
          setBreakDuration(0)
        }
      })
      console.log(currentSession)
      setCount(count+ .065)
    },

    isTimerRunning ? 1 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }



  return (
    <div className="pomodoro">

      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={ () => setIsTimerRunning(false)}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">{currentSession}</h2>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration(timer)} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="1500"
                aria-valuenow= {count} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: `${count}%` }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
